"use strict";var app=angular.module("lnnowak.colorsBalance",[]);app.controller("balanceCtrl",["$scope",function(n){n.colors={red:0,green:0,blue:0};n.newColors={red:0,green:0,blue:0};n.calculateColors=function(){var t=(n.colors.red-n.colors.blue)/2;n.newColors.red=n.colors.green+t;n.newColors.blue=n.colors.green-t;n.newColors.green=n.colors.green}}]);